import axios from "axios";
const {
  PRODUCT_LIST_REQUEST,
  PRODUCT_LIST_SUCCESS,
  PRODUCT_LIST_FAIL,
  PRODUCT_DETAILS_REQUEST,
  PRODUCT_DETAILS_SUCCESS,
  PRODUCT_DETAILS_FAIL,
  PRODUCT_SAVE_SUCCESS,
  PRODUCT_SAVE_FAIL,
  PRODUCT_SAVE_REQUEST,
  PRODUCT_DELETE_REQUEST,
  PRODUCT_DELETE_SUCCESS,
  PRODUCT_DELETE_FAIL,
} = require("../constants/productConstants");

const listProduct = () => async (dispatch) => {
  try {
    dispatch({ type: PRODUCT_LIST_REQUEST });
    axios
      .get("http://localhost:5000/api/products")
      .then((response) =>
        dispatch({ type: PRODUCT_LIST_SUCCESS, payload: response.data })
      );
  } catch (error) {
    dispatch({ type: PRODUCT_LIST_FAIL, payload: error.response.data.message });
  }
};

const saveProduct = (product) => async (dispatch, getState) => {
  dispatch({ type: PRODUCT_SAVE_REQUEST, payload: product });
  const {
    userSignin: { userInfo },
  } = getState();
  if (!product._id) {
    await axios
      .post("http://localhost:5000/api/products", product, {
        headers: {
          Authorization: "Bearer " + userInfo.token,
        },
      })
      .then((response) =>
        dispatch({ type: PRODUCT_SAVE_SUCCESS, payload: response.data })
      )
      .catch((error) =>
        dispatch({
          type: PRODUCT_SAVE_FAIL,
          payload: error.response.data.message,
        })
      );
  } else {
    console.log("product._id", product._id);
    await axios
      .put("http://localhost:5000/api/products/" + product._id, product, {
        headers: {
          Authorization: "Bearer " + userInfo.token,
        },
      })
      .then((response) =>
        dispatch({ type: PRODUCT_SAVE_SUCCESS, payload: response.data })
      )
      .catch((error) =>
        dispatch({
          type: PRODUCT_SAVE_FAIL,
          payload: error.response.data.message,
        })
      );
  }
};

const detailsProduct = (productId) => async (dispatch) => {
  dispatch({ type: PRODUCT_DETAILS_REQUEST, payload: productId });
  axios
    .get("http://localhost:5000/api/products/" + productId)
    .then((response) => {
      dispatch({ type: PRODUCT_DETAILS_SUCCESS, payload: response.data });
    })
    .catch((error) =>
      dispatch({
        type: PRODUCT_DETAILS_FAIL,
        payload: error.response.data.message,
      })
    );
};

const deleteProduct = (productId) => async (dispatch, getState) => {
  const {
    userSignin: { userInfo },
  } = getState();
  dispatch({ type: PRODUCT_DELETE_REQUEST, payload: productId });
  axios
    .delete("http://localhost:5000/api/products/" + productId, {
      headers: {
        Authorization: "Bearer" + userInfo.token,
      },
    })
    .then((response) => {
      dispatch({
        type: PRODUCT_DELETE_SUCCESS,
        payload: response.data,
        success: true,
      });
    })
    .catch((error) =>
      dispatch({
        type: PRODUCT_DELETE_FAIL,
        payload: error.response.data.message,
      })
    );
};

export { listProduct, detailsProduct, saveProduct, deleteProduct };

